pipeline {
    agent any
    stages {
        stage('Build') {
            steps {
                sh 'make'
            }
        }
    }
}

agent {
    label 'linux && docker'
}
# Ensure agent has these labels

withCredentials([usernamePassword(
    credentialsId: 'my-cred-id',  // Exact ID
    usernameVariable: 'USER',
    passwordVariable: 'PASS'
)]) {
    sh 'login -u $USER -p $PASS'
}

environment {
    MY_SECRET = credentials('my-secret-id')
}
// Access: $MY_SECRET or $MY_SECRET_USR/$MY_SECRET_PSW

stage('Checkout') {
    steps {
        checkout scm
        // or: git branch: 'main', url: 'https://...'
    }
}

dir('subdirectory') {
    sh 'make'
}
// Or: pwd(), WORKSPACE env var

options {
    cleanWs()  // Clean before build
}
// Or: deleteDir() in post section

options {
    timeout(time: 1, unit: 'HOURS')
}

timeout(time: 10, unit: 'MINUTES') {
    sh './slow-script.sh'
}

withCredentials([string(credentialsId: 'github-token', variable: 'TOKEN')]) {
    sh 'git clone https://${TOKEN}@github.com/org/repo.git'
}

parallel failFast: false,
    'branch1': { ... },
    'branch2': { ... }

parallel(
    'unit-tests': {
        catchError(buildResult: 'SUCCESS', stageResult: 'FAILURE') {
            sh 'make test'
        }
    }
)
